import './App.css';
import RulesScreen from './components/RulesScreen';
import GameScreen from './components/GameScreen';
import { useEffect,useState } from 'react';

function App() {
  const [showRules, setShowRules] = useState(true);

  return (
    <div className="App">
      <main>
        {}
        <header>
          <img src='/images/rules/logo-bonus.svg'/>
          <div id='score'>
            <p className='score_tittle'>score</p>
            <p className='points'>12</p>
          </div>
        </header>
        <GameScreen className="game"/>
        <div id='div_button_rules'>
          <button onClick={()=> setShowRules(true)} className='btn_rules'>Rules</button>
        </div>
       {showRules && <RulesScreen setShowRules={setShowRules}/>
       }
      </main>
      <footer>Challenge by <a href="" target='_blank'>Frontend Mentor</a>. Coded by <a href="" target='_blank'>Lais Talita</a>. </footer>
    </div>
  );
}

export default App;
