import './GameScreen.css'

function ResultScreen({playerChoice, playAgain}) {
     const cards_buttons=["icon-lizard.svg", "icon-paper.svg","icon-rock.svg","icon-scissors.svg","icon-spock.svg"]

     cards_buttons.forEach(element => {
        if (element === playAgain) {
            let element = playAgain
        }
     });
    return(
         <section className="game_screen">
            {
              cards_buttons.map(choice =>(
                <button key={choice}>
                <img src={`/images/rules/${choice}`}/>
                </button>
              ))
            }
        </section>
    )
}export default ResultScreen
