const Colors = {
  "icon-lizard.svg": {
    gradient: "radial-gradient(circle, white 53%, hsl(261, 73%, 60%) 10%)",
    border: "hsl(261, 60%, 45%)" // cor mais escura para borda
  },
  "icon-paper.svg": {
    gradient: "radial-gradient(circle, white 53%, hsl(230, 89%, 62%) 10%)",
    border: "hsl(230, 89%, 45%)"
  },
  "icon-rock.svg": {
    gradient: "radial-gradient(circle, white 53%, hsl(349, 71%, 52%) 10%)",
    border: "hsl(349, 70%, 40%)"
  },
  "icon-scissors.svg": {
    gradient: "radial-gradient(circle, white 53%, hsl(39, 89%, 49%) 10%)",
    border: "hsl(39, 89%, 35%)"
  },
  "icon-spock.svg": {
    gradient: "radial-gradient(circle, white 53%, hsl(189, 59%, 53%) 10%)",
    border: "hsl(189, 59%, 40%)"
  }
};

export default Colors;
