import './RulesScreen.css'
function RulesScreen() {
    return(
        <section id='rules'>
            <div className='rules'>
                <h1>Rules</h1>
                 <button><img src='/images/rules/icon-close.svg' alt='close rules'/></button>
            </div>
            <img src="/images/rules/image-rules-bonus.svg" alt='rules image'/>
            
        </section>
    )
}export default RulesScreen
